// this export breaks fast-refresh
export const iconTypes = Object.keys(iconMap) as IconName[]

export type IconName = keyof typeof iconMap

export type IconProps = {
    type: IconName
    background?: keyof typeof vars.color.background
    color?: BoxProps['color']
    padding?: BoxProps['padding']
    className?: string
} & IconAtoms

export const Icon = forwardRef<HTMLDivElement, IconProps>((props, ref) => {
    const {
        size = 'square_md',
        color,
        background,
        type,
        // eslint-disable-next-line @typescript-eslint/no-unused-vars
        height,
        className,
        ...boxProps
    } = props
    const Icon = iconMap[type ?? 'bell']

    return (
        <Box
            shrink
            centerContent
            aspectRatio="square"
            className={clsx(iconBaseStyle, iconAtoms({ size }), className)}
            background={background}
            color={color}
            padding={
                props.padding ??
                (!background || background?.match(/^(none|transparent|default)$/) ? 'none' : 'xs')
            }
            ref={ref}
            {...boxProps}
        >
            <Icon width="100%" height="100%" />
        </Box>
    )
})
